mapper.select:
select * from tb_user
where phone = #{phone}
and password = #{password}

select * from tb_user
where user_id = #{user_id}
and password = #{password}

mapper.update:
       UPDATE tb_user

           username = #{username},
           sex = #{sex},
           headPortrait = #{headPortrait}

	   where user_id = #{user_id}
